+            await remove(ref(db, `meals/${mealId}`));
+            fetchMeals(); // refresh
+          } catch (error) {
+            console.error('üî• Failed to delete meal:', error);
+            Alert.alert('Error', 'Failed to delete meal.');
+          }
+        },
+      },
+    ]);
+  };
+
+  useEffect(() => {
+    fetchMeals();
+  }, []);
+
+  const onRefresh = useCallback(() => {
+    setRefreshing(true);
+    fetchMeals();
+  }, []);
+
+  if (loading) {
+    return (
+      <View style={styles.center}>
+        <ActivityIndicator size="large" />
+      </View>
+    );
+  }
+
+  return (
+    <View style={styles.container}>
+      {/* Meals You Created */}
+      <Text style={styles.header}>üìù Meals You Created</Text>
+      <FlatList
+        data={myCreatedMeals}
+        keyExtractor={(item, index) => item.id ?? index.toString()}
+        renderItem={({ item }) => (
+          <View style={[styles.card, styles.cardRow]}>
+            <Pressable
+              onPress={() => navigation.navigate('EditMeal', { meal: item })}
+              style={styles.cardContent}
+            >
+              <Text style={styles.cardTitle}>{item.title}</Text>
+              <Text>{item.location} ‚Ä¢ {item.time}</Text>
+            </Pressable>
+            <Pressable
+              onPress={() => handleDeleteMeal(item.id)}
+              style={styles.deleteButton}
+            >
+              <Text style={styles.deleteText}>Delete</Text>
+            </Pressable>
+          </View>
+        )}
+        ListEmptyComponent={
+          <Text style={styles.emptyText}>
+            You haven‚Äôt created any meals yet.
+          </Text>
+        }
+      />
+
+      {/* Meals You Joined */}
+      <Text style={styles.header}>üçΩ Meals You Joined</Text>
+      <FlatList
+        data={joinedMeals}
+        keyExtractor={(item, index) => item.id ?? index.toString()}
+        refreshControl={
+          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
+        }
+        renderItem={({ item }) => (
+          <Pressable
+            onPress={() => navigation.navigate('EditMeal', { meal: item })}
+            style={styles.card}
+          >
+            <Text style={styles.cardTitle}>{item.title}</Text>
+            <Text>{item.location} ‚Ä¢ {item.time}</Text>
+          </Pressable>
+        )}
+        ListEmptyComponent={
+          <Text style={styles.emptyText}>
+            You haven't joined any meals yet.
+          </Text>
+        }
+      />
+    </View>
+  );
+}
